import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ argumentSchema, handleNodeClick }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(svgRef.current);

    // Adjust width and height of the SVG container
    const containerWidth = window.innerWidth * 0.9; // 90% of the window width
    const containerHeight = 300; // Fixed height

    svg.attr('width', containerWidth)
       .attr('height', containerHeight);

    // Rest of your D3.js code for rendering nodes and handling interactions
    // ...

  }, [argumentSchema]);

  return (
    <svg ref={svgRef} style={{ border: '1px solid #ccc' }}>
      {/* Nodes and links rendering code */}
    </svg>
  );
};

export default Graph;
