import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ argumentSchema }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    const width = +svg.attr('width');
    const height = +svg.attr('height');

    // Clear existing content
    svg.selectAll('*').remove();

    // Create nodes and links
    const nodes = argumentSchema ? [{ id: argumentSchema._id, label: argumentSchema.title }] : [];
    const links = [];

    // Draw nodes
    const node = svg
      .append('g')
      .selectAll('circle')
      .data(nodes)
      .join('circle')
      .attr('r', 20) // Increase node size for visibility
      .attr('fill', '#1f77b4')
      .call(d3.drag()
        .on('start', (event, d) => dragstarted(event, d))
        .on('drag', (event, d) => dragged(event, d))
        .on('end', (event, d) => dragended(event, d)));

    // Add labels to nodes
    node
      .append('title')
      .text(d => d.label);

    // Set initial node positions
    node.attr('cx', width / 2).attr('cy', height / 2);

    function dragstarted(event, d) {
      d3.select(this).raise().classed('active', true);
    }

    function dragged(event, d) {
      d3.select(this).attr('cx', event.x).attr('cy', event.y);
    }

    function dragended(event, d) {
      d3.select(this).classed('active', false);
    }
  }, [argumentSchema]);

  return (
    <svg ref={svgRef} width={400} height={300} style={{ border: '1px solid #ccc' }}>
      <defs>
        <marker id="arrow" viewBox="0 -5 10 10" refX="8" refY="0" markerWidth="6" markerHeight="6" orient="auto">
          <path d="M0,-5L10,0L0,5" fill="#999" />
        </marker>
      </defs>
    </svg>
  );
};

export default Graph;
