import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ argumentSchema, handleNodeClick }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(svgRef.current);

    // Adjust width and height of the SVG container
    const containerWidth = window.innerWidth * 0.9; // 90% of the window width
    const containerHeight = window.innerHeight * 0.85; // Fixed height

    svg.attr('width', containerWidth)
      .attr('height', containerHeight);

    // Clear existing content
    svg.selectAll('*').remove();

    // Render nodes
    if (argumentSchema && argumentSchema._id) {
      const startingNode = { id: argumentSchema._id, label: argumentSchema._id };

      // Log ID and label of the current node
      console.log("Starting Node:", startingNode);

      svg.append('circle')
        .attr('cx', containerWidth / 3)
        .attr('cy', containerHeight / 2)
        .attr('r', 20)
        .attr('fill', '#1f77b4')
        .on('click', () => {
          if (handleNodeClick) {
            handleNodeClick(startingNode.id);
          }
        });

      // Render nodes for each element in isAttackedBy
      if (argumentSchema.isAttackedBy) {

        const attackersCount = argumentSchema.isAttackedBy.length;
        const attackerSpacing = 50;

        argumentSchema.isAttackedBy.forEach((attackerId, index) => {
          const attackerNode = { id: attackerId, label: attackerId }; // Assuming label is same as ID

          // Log ID and label of attacker node
          console.log("Attacker Node:", attackerNode);

          svg.append('circle')
            .attr('cx', containerWidth / 3 + attackerSpacing * (index + 1)) // Position attacker nodes to the right side with spacing
            .attr('cy', containerHeight / 2) // Randomize Y position for demo
            .attr('r', 20)
            .attr('fill', '#ff0000') // Red color for attacker nodes
            .on('click', () => {
              if (handleNodeClick) {
                handleNodeClick(attackerNode.id);
              }
            });
        });
      }
    }

  }, [argumentSchema, handleNodeClick]);

  return (
    <svg ref={svgRef} style={{ border: '1px solid #ccc' }}>
      {/* Nodes and links rendering code */}
    </svg>
  );
};

export default Graph;
