import React from 'react';

const Graph = ({ argumentSchema, handleNodeClick }) => {
  const handleCircleClick = () => {
    // Check if handleNodeClick function is provided
    if (handleNodeClick) {
      // Invoke the handleNodeClick function with appropriate arguments
      handleNodeClick(argumentSchema._id);
    }
  };

  return (
    <svg width={400} height={300} style={{ border: '1px solid #ccc' }}>
      {argumentSchema && (
        <circle
          cx={200}
          cy={150}
          r={20}
          fill="#1f77b4"
          onClick={handleCircleClick}
          style={{ cursor: 'pointer' }} // Change cursor to pointer when hovering over the circle
        />
      )}
    </svg>
  );
};

export default Graph;
