import React from 'react';

const Graph = ({ argumentSchema, handleNodeClick }) => {
  const handleCircleClick = () => {
    if (handleNodeClick) {
      handleNodeClick(argumentSchema._id);
    }
  };

  return (
    <svg
      width="90vw"  // Set width to 90% of the viewport width
      height={300}   // Set a fixed height of 300 pixels
      style={{ border: '1px solid #ccc' }}
    >
      {argumentSchema && (
        <circle
          cx="50%"     // Position circle at the center horizontally
          cy="50%"     // Position circle at the center vertically
          r={20}
          fill="#1f77b4"
          onClick={handleCircleClick}
          style={{ cursor: 'pointer' }}
        />
      )}
    </svg>
  );
};

export default Graph;
