import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ startingNodeId, handleNodeClick }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/argumentSchemas`);
        if (!response.ok) {
          throw new Error('Failed to fetch argument schemas');
        }
        const argumentSchemas = await response.json();
        const argumentSchema = argumentSchemas.find(schema => schema._id === startingNodeId);
        if (!argumentSchema) {
          throw new Error('Argument schema not found');
        }
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove(); // Clear previous elements
        const nodesGroup = svg.append('g');

        const renderNodes = (node, x, y, nodesGroup) => {
          if (!node || !node._id) return;

          const radius = 20;
          const attackerSpacing = 100;

          nodesGroup.append('circle')
            .attr('cx', x)
            .attr('cy', y)
            .attr('r', radius)
            .attr('fill', '#1f77b4')
            .on('click', () => {
              if (handleNodeClick) {
                handleNodeClick(node._id);
              }
            });

          if (node.isAttackedBy && node.isAttackedBy.length > 0) {
            const initialY = y - (node.isAttackedBy.length - 1) * attackerSpacing / 2;

            node.isAttackedBy.forEach((attackerId, index) => {
              const attackerNode = argumentSchemas.find(schema => schema._id === attackerId);

              const attackerY = initialY + index * attackerSpacing;

              const arrowStartX = x + radius;
              const arrowStartY = y;
              const arrowEndX = x + attackerSpacing;
              const arrowEndY = attackerY;

              const angle = Math.atan2(arrowEndY - arrowStartY, arrowEndX - arrowStartX);
              const arrowHeadSize = 40;

              const arrow = d3.symbol().type(d3.symbolTriangle).size(arrowHeadSize);
              const lineData = [
                [arrowStartX, arrowStartY],
                [arrowEndX, arrowEndY]
              ];

              nodesGroup.append('path')
                .attr('d', arrow)
                .attr('transform', `translate(${arrowEndX},${arrowEndY}) rotate(${angle * (180 / Math.PI) + 90})`)
                .attr('fill', '#000000');

              nodesGroup.append('path')
                .attr('d', d3.line()(lineData))
                .attr('stroke', '#000000')
                .attr('stroke-width', 2);

              renderNodes(attackerNode, arrowEndX, arrowEndY, nodesGroup);
            });
          }
        };

        renderNodes(argumentSchema, window.innerWidth / 3, window.innerHeight / 2, nodesGroup);
      } catch (error) {
        console.error('Error fetching argument schemas:', error);
      }
    };

    fetchData();
  }, [startingNodeId, handleNodeClick]);

  return (
    <svg ref={svgRef} width={window.innerWidth} height={window.innerHeight} style={{ border: '1px solid #ccc' }} />
  );
};

export default Graph;
