// ArgumentNode.js
import React from 'react';

const ArgumentNode = ({ argumentId, onClick, clicked }) => {

    const nodeSize = 50; // Adjust the size of the node as needed

    return (
        <div style={{ margin: '10px', cursor: 'pointer' }} onClick={() => onClick(argumentId)}>
            <svg width={nodeSize} height={nodeSize}>
                {/* Apply red border when selected */}
                <circle cx={nodeSize / 2} cy={nodeSize / 2} r={nodeSize / 2} fill="lightblue" stroke={clicked ? 'red' : 'none'} strokeWidth="2" />
                <text x="50%" y="50%" textAnchor="middle" dominantBaseline="middle">{argumentId}</text>
            </svg>
        </div>
    );
}

export default ArgumentNode;
