import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const Graph = ({ argumentSchema, handleNodeClick }) => {
  const svgRef = useRef(null);
  const [clickedNode, setClickedNode] = useState(null);
  const [replyButtonClicked, setReplyButtonClicked] = useState(false);

  useEffect(() => {
    const svg = d3.select(svgRef.current);

    // Adjust width and height of the SVG container
    const containerWidth = window.innerWidth * 0.9; // 90% of the window width
    const containerHeight = 300; // Fixed height

    svg.attr('width', containerWidth)
       .attr('height', containerHeight);

    // Clear existing content
    svg.selectAll('*').remove();

    // Render nodes
    if (argumentSchema) {
      const nodes = [{ id: argumentSchema._id, label: argumentSchema.title }];

      svg.selectAll('circle')
         .data(nodes)
         .enter()
         .append('circle')
         .attr('cx', containerWidth / 2)
         .attr('cy', containerHeight / 2)
         .attr('r', 20)
         .attr('fill', '#1f77b4')
         .attr('stroke', d => d.id === clickedNode && !replyButtonClicked ? 'red' : 'none')
         .attr('stroke-width', 2)
         .on('click', (event, d) => {
           setClickedNode(prevClickedNode => prevClickedNode === d.id ? null : d.id);
           handleNodeClick(d.id);
         });
    }

  }, [argumentSchema, clickedNode, handleNodeClick, replyButtonClicked]);

  const handleReplyButtonClick = () => {
    setReplyButtonClicked(true);
  };

  const handleCancelButtonClick = () => {
    setClickedNode(null);
    setReplyButtonClicked(false);
  };

  return (
    <svg ref={svgRef} style={{ border: '1px solid #ccc' }}>
      {replyButtonClicked && (
        <button onClick={handleCancelButtonClick}>Cancel</button>
      )}
    </svg>
  );
};

export default Graph;
