import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ startingNodeId, handleNodeClick }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/argumentSchemas`);
        if (!response.ok) {
          throw new Error('Failed to fetch argument schemas');
        }
        const argumentSchemas = await response.json();
        const argumentSchema = argumentSchemas.find(schema => schema._id === startingNodeId);
        if (!argumentSchema) {
          throw new Error('Argument schema not found');
        }
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove();
        const nodesGroup = svg.append('g');

        const renderedNodes = new Set();

        const renderNodes = (node, x, y, nodesGroup) => {
          if (!node || !node._id || renderedNodes.has(node._id)) return;

          const radius = 20;
          const attackerSpacing = 150;

          if (!renderedNodes.has(node._id)) {

            nodesGroup.append('circle')
              .attr('cx', x)
              .attr('cy', y)
              .attr('r', radius)
              .attr('fill', '#1f77b4')
              .on('click', () => {
                if (handleNodeClick) {
                  handleNodeClick(node._id);
                }
              });

            renderedNodes.add(node._id);
          }

          if (node.isAttackedBy && node.isAttackedBy.length > 0) {
            const initialY = y - (node.isAttackedBy.length - 1) * attackerSpacing / 2;

            node.isAttackedBy.forEach((attackerId, index) => {

              if (!renderedNodes.has(attackerId)) {
                const attackerNode = argumentSchemas.find(schema => schema._id === attackerId);

                const attackerY = initialY + index * attackerSpacing;

                const arrowStartX = x;
                const arrowStartY = y;
                const arrowEndX = x + (attackerSpacing - radius);
                const arrowEndY = attackerY;

                const lineLength = Math.sqrt((arrowEndX - arrowStartX) ** 2 + (arrowEndY - arrowStartY) ** 2);

                const arrowheadX = arrowStartX + (radius / lineLength) * (arrowEndX - arrowStartX);
                const arrowheadY = arrowStartY + (radius / lineLength) * (arrowEndY - arrowStartY);

                const angle = Math.atan2(arrowEndY - arrowStartY, arrowEndX - arrowStartX);

                nodesGroup.append('line')
                  .attr('x1', arrowheadX)
                  .attr('y1', arrowheadY)
                  .attr('x2', arrowEndX)
                  .attr('y2', arrowEndY)
                  .attr('stroke', '#000000')
                  .attr('stroke-width', 2);

                nodesGroup.append('path')
                  .attr('d', d3.symbol().type(d3.symbolTriangle).size(80))
                  .attr('transform', `translate(${arrowheadX},${arrowheadY}) rotate(${angle * (180 / Math.PI) - 90})`)
                  .attr('fill', '#000000');

                const criticalQuestionId = attackerNode.chosenCriticalQuestion;

                if (criticalQuestionId === 3 || criticalQuestionId === 8) {

                  const midX = (arrowStartX + arrowEndX) / 2;
                  const midY = (arrowStartY + arrowEndY) / 2;

                  const secondArrowheadAngle = Math.atan2(arrowEndY - arrowStartY, arrowEndX - arrowStartX);

                  const secondArrowheadDistance = 30;
                  const secondArrowheadX = midX + secondArrowheadDistance * Math.cos(secondArrowheadAngle);
                  const secondArrowheadY = midY + secondArrowheadDistance * Math.sin(secondArrowheadAngle);

                  nodesGroup.append('path')
                    .attr('d', d3.symbol().type(d3.symbolTriangle).size(80))
                    .attr('transform', `translate(${secondArrowheadX},${secondArrowheadY}) rotate(${secondArrowheadAngle * (180 / Math.PI) + 90})`)
                    .attr('fill', '#000000');
                }

                renderNodes(attackerNode, arrowEndX, arrowEndY, nodesGroup);
              }
            });
          }
        };

        renderNodes(argumentSchema, window.innerWidth / 3, window.innerHeight / 2, nodesGroup);
      } catch (error) {
        console.error('Error fetching argument schemas:', error);
      }
    };

    fetchData();
  }, [startingNodeId, handleNodeClick]);

  const handleEvaluateGraphClick = async (startingNodeId) => {
    try {

      const rendered_nodes = new Set()

      const renderNodes = (node, nodesGroup) => {
        if (!node || !node._id || rendered_nodes.has(node._id)) return;

        if (!rendered_nodes.has(node._id)) {
          rendered_nodes.add(node._id);
        }

        if (node.isAttackedBy && node.isAttackedBy.length > 0) {

          node.isAttackedBy.forEach((attackerId, index) => {

            if (!rendered_nodes.has(attackerId)) {
              const attackerNode = argumentSchemas.find(schema => schema._id === attackerId);
              renderNodes(attackerNode, nodesGroup);
            }
          });
        }
      };

      renderNodes(startingNodeId)
      console.log('Evaluate Graph button has been clicked');

    } catch (error) {
      console.error('Error fetching argument schemas:', error);
    }
  };

  return (
    <div style={{ position: 'relative', width: window.innerWidth * 0.95, height: window.innerHeight * 0.9 }}>
      <svg ref={svgRef} width="100%" height="100%" style={{ border: '1px solid #ccc' }} />
      <button
        onClick={handleEvaluateGraphClick}
        style={{
          position: 'absolute',
          top: 0,
          right: 0,
          padding: '10px',
          margin: '10px',
          zIndex: 1000 // Ensure the button is above all other elements
        }}
      >
        Evaluate Graph
      </button>
    </div>
  );
};

export default Graph;
