import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Graph = ({ argumentSchema }) => {
  const svgRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    const width = +svg.attr('width');
    const height = +svg.attr('height');

    // Clear existing content
    svg.selectAll('*').remove();

    // Create a simulation
    const simulation = d3
      .forceSimulation()
      .force('link', d3.forceLink().id(d => d.id))
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2));

    // Create nodes and links
    const nodes = argumentSchema ? [{ id: argumentSchema._id, label: argumentSchema.title }] : [];
    const links = [];

    // Draw links
    const link = svg
      .append('g')
      .attr('stroke', '#999')
      .attr('stroke-opacity', 0.6)
      .selectAll('line')
      .data(links)
      .join('line');

    // Draw nodes
    const node = svg
      .append('g')
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5)
      .selectAll('circle')
      .data(nodes)
      .join('circle')
      .attr('r', 20) // Increase node size for visibility
      .attr('fill', '#1f77b4');

    // Add labels to nodes
    node
      .append('title')
      .text(d => d.label);

    // Add drag behavior
    node.call(d3.drag()
      .on('start', dragstarted)
      .on('drag', dragged)
      .on('end', dragended));

    // Add simulation
    simulation.nodes(nodes).on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      node
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    });

    simulation.force('link').links(links);

    return () => {
      simulation.stop();
    };

    function dragstarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }

    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }

    function dragended(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }
  }, [argumentSchema]);

  return (
    <svg ref={svgRef} width={400} height={300} style={{ border: '1px solid #ccc' }}>
      <defs>
        <marker id="arrow" viewBox="0 -5 10 10" refX="8" refY="0" markerWidth="6" markerHeight="6" orient="auto">
          <path d="M0,-5L10,0L0,5" fill="#999" />
        </marker>
      </defs>
    </svg>
  );
};

export default Graph;
